// Get input from parent workflow
const input = $input.first().json;

// Initialize variables for pagination with input from parent
const baseUrl = "https://www.googleapis.com/customsearch/v1";
const cx = "cxIdNumber";
const query = input.query || "(intitle:\"roleTitle1\" OR intitle:\"roleTitle2\") AND intext:onPageText";

  // Use siteSearch from input, with fallback
const siteSearch = input.siteSearch || "ashbyhq.com";

// Set initial values
return [{
  json: {
    cx: cx,
    query: query,
    siteSearch: siteSearch,
    start: 1,
    allResults: [],
    hasMoreResults: true,
    totalResults: 0,
    currentPage: 1,
    inputData: input // Store original input for reference
  }
}];

// Get the current search response
const currentData = $input.first().json;
const items = currentData.items || [];
const searchInfo = currentData.searchInformation || {};
const totalResults = parseInt(searchInfo.totalResults) || 0;
const resultsPerPage = 10;


// Get static values from Initialize (never change)
const initData = $("Initialize with Input").first().json;

// Get dynamic state: try from previous Process Results, fallback to Initialize
let allResults, currentStart, currentPage, inputData;

if (($('Process Results').isExecuted)) {

    // Get accumulated state from previous Process Results iteration
    const lastResults = $("Process Results").first().json;

    allResults = lastResults.allResults || [];
    currentStart = lastResults.start || 1;
    currentPage = lastResults.currentPage || 1;
} else {
    // First iteration - use initial values
    allResults = initData.allResults || [];
    currentStart = initData.start || 1;
    currentPage = initData.currentPage || 1;
}

// âœ… FIX: Get accumulated state from the data that was sent to HTTP request
//const previousState = $("Google Search Request").first().json;
//
// Get the existing accumulated results
//let allResults = previousState.allResults || [];
//let currentStart = previousState.start || 1;
//let currentPage = previousState.currentPage || 1;

// Add NEW results to existing collection
allResults = allResults.concat(items);

// Calculate next iteration
const nextStart = currentStart + resultsPerPage;
const hasMoreResults = nextStart <= Math.min(totalResults, 100) && items.length === resultsPerPage;
const shouldContinue = hasMoreResults && allResults.length < 100;

console.log(`Page ${currentPage}: Added ${items.length} results. Total: ${allResults.length}`);

return [{
  json: {
    start: nextStart,
    allResults: allResults, // Now accumulates properly!
    hasMoreResults: shouldContinue,
    totalResults: totalResults,
    currentPage: currentPage + 1,
    currentBatchSize: items.length,
    totalCollected: allResults.length
  }
}];

// Prepare final output for parent workflow
const finalData = $input.first().json;

// Return the results in a format expected by parent workflow
return [{
  json: {
    success: true,
    siteSearch: finalData.siteSearch,
    searchSummary: {
      totalResultsFound: finalData.totalResults,
      totalResultsCollected: finalData.totalCollected,
      pagesProcessed: finalData.currentPage - 1,
      query: finalData.query,
      siteSearch: $('Initialize with Input').first().json.siteSearch
    },
    allResults: finalData.allResults,
    originalInput: finalData.inputData
  }
}];
